import Axios from "axios";
import {
  Form,
  Button,
  ButtonGroup,
  Row,
  Col,
  Alert,
  InputGroup,
} from "react-bootstrap";
import { useState, useEffect } from "react";
import { View, Text } from "react-native";
import { Link, useNavigate } from "react-router-dom";
import useAuth from "../hooks/useAuth";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faEyeSlash,
  faEye,
  faLock,
  faLockOpen,
  faEnvelope,
} from "@fortawesome/free-solid-svg-icons";

const SignIn = () => {
  const { auth, setAuth } = useAuth();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPass, setShowPass] = useState(false);
  const [message, setMessage] = useState("");
  const [showMessage, setShowMessage] = useState(false);
  const [color, setColor] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (auth?.roles?.includes("manager")) {
      navigate("/ManagerInterface");
    } else if (auth?.roles?.includes("programmer")) {
      navigate("/ProgrammerInterface");
    } else if (auth?.roles?.includes("user")) {
      navigate("/UserForm");
    } else {
      navigate("/SignIn");
    }
  }, []);

  const logIn = (e) => {
    e.preventDefault();

    Axios.post("http://localhost:3001/signIn", {
      email: email,
      password: password,
    })
      .then((res) => {
        console.log(res.data);
        if (res.status === 200) {
          setMessage("Successfully Logined");
          setColor("success");
          setShowMessage(true);
          clearForm(e);
          const roles = res?.data?.roles;
          const id = res?.data?.id;
          const userName = res?.data?.userName;
          const firstName = res?.data?.firstName;
          const lastName = res?.data?.lastName;
          setAuth({
            email,
            password,
            roles,
            id,
            userName,
            firstName,
            lastName,
          });
          //console.log(auth?.roles);
          // if (roles?.includes("manager")) {
          //   navigate("/ManagerInterface");
          // } else if (roles?.includes("programmer")) {
          //   navigate("/ProgrammerInterface");
          // } else if (roles?.includes("user")) {
          //   navigate("/UserForm");
          // } else {
          //   navigate("/Unauthorized");
          // }
        }
      })
      .catch((err) => {
        console.log(err);
        setColor("danger");
        setShowMessage(true);
        if (!err?.response) setMessage("No Server Response");
        else if (err?.response?.status === 409)
          setMessage("Either Email, password or both are wrong");
        else setMessage("Someting went wrong, try again later");
      });
  };

  const clearForm = (e) => {
    setEmail("");
    setPassword("");
            setShowMessage();

  };

  return (
    <Form className="mx-auto mt-5" style={{ width: "680px" }}>
      <h1 className="text-center mb-4">Sign In Form</h1>
      <Alert
        className="pb-0"
        variant={color}
        show={message !== "" && showMessage}
      >
        <Alert.Heading>SignIn Message</Alert.Heading>
        <p>{message}</p>
      </Alert>
      <Row className="mb-3">
        <Form.Group as={Col}>
          <Form.Label>Email address</Form.Label>
          <InputGroup>
            <InputGroup.Text variant="outline-light">
              <FontAwesomeIcon icon={faEnvelope} />
            </InputGroup.Text>
            <Form.Control
              type="text"
              placeholder="Enter your Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{ boxShadow: "none" }}
              required
            />
          </InputGroup>
        </Form.Group>
      </Row>
      <Row className="mb-3">
        <Form.Group as={Col}>
          <Form.Label>Password</Form.Label>
          <InputGroup>
            <InputGroup.Text variant="outline-light">
              <FontAwesomeIcon icon={showPass ? faLockOpen : faLock} />
            </InputGroup.Text>
            <Form.Control
              type={showPass ? "text" : "password"}
              placeholder="Enter your Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{ boxShadow: "none" }}
              required
            />
            <Button
              variant="outline-secondary"
              onClick={() => setShowPass(!showPass)}
              style={{ boxShadow: "none" }}
            >
              <FontAwesomeIcon icon={showPass ? faEye : faEyeSlash} />
            </Button>
          </InputGroup>
        </Form.Group>
      </Row>
      <Row>
        <Col>
          <ButtonGroup className="d-flex">
            <Button
              variant="primary"
              type="submit"
              disabled={email === "" || password === ""}
              onClick={logIn}
              style={{ boxShadow: "none" }}
            >
              Submit
            </Button>
          </ButtonGroup>
        </Col>
      </Row>
      <Row className="m-3">
        <View style={{ flexDirection: "row", alignItems: "center" }}>
          <View style={{ flex: 1, height: 2, backgroundColor: "black" }} />
          <View>
            <Text style={{ width: 50, textAlign: "center" }}>OR</Text>
          </View>
          <View style={{ flex: 1, height: 2, backgroundColor: "black" }} />
        </View>
      </Row>
      <Row>
        <Form.Group as={Col} style={{ textAlign: "center" }}>
          <p>
            Does not have an account yet?<br></br>
            Sign up now.
          </p>
        </Form.Group>
        <Col>
          <Link to="/SignUp" style={{ textDecoration: "none", color: "white" }}>
            <ButtonGroup className="d-flex">
              <Button variant="success" style={{ boxShadow: "none" }}>
                SignUp
              </Button>
            </ButtonGroup>
          </Link>
        </Col>
      </Row>
    </Form>
  );
};

export default SignIn;
